import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import Mychart from "./mychart";

const Vehicle = props => (
  <tr>
    <td>{props.vehicle.no}</td>
    <td>{props.vehicle.make}</td>
    <td>{props.vehicle.carmodel}</td>
    <td>{props.vehicle.year}</td>
    <td>{props.vehicle.price}</td>
    <td>{props.vehicle.status}</td>
    <td>
      <Link to={"/edit/" + props.vehicle._id}>edit</Link>
    </td>
  </tr>
)

export default class MasterVehicle extends Component {

  constructor(props) {
    super(props);

    this.vehicleList = this.vehicleList.bind(this);

    this.state = { vehicles: [] };
  }

  componentDidMount() {
    axios.get('http://localhost:5000/vehicles/')
      .then(response => {
        this.setState({ vehicles: response.data })
      })
      .catch((error) => {
        console.log(error);
      })
  }

  vehicleList() {
    return this.state.vehicles.map(currentvehicle => {
      return <Vehicle vehicle={currentvehicle} key={currentvehicle._id} />;
    })
  }

  vehicleSearch = () => {
    const { vehicles, term } = this.state;

    if (!term) {
      return vehicles.map(function(currentVehicle, i) {
        return <Vehicle Vehicle={currentVehicle} key={i} />;
      });
    } else {
      return vehicles
        .filter(Vehicle => {
          return Vehicle.no == term;
        })
        .map(matchingVehicle => {
          return <Vehicle Vehicle={matchingVehicle} />;
        });
    }
  };

  onSearchHandler = e => {
    this.setState({
      term: e.target.value
    });
  };

  render() {
    return (
      <div className="container">
        <Mychart />
        <br />
        <h3>Chanakya's Vehicle Database</h3>
        <div className="row">
          <form>
            <input 
              type="text"
              onChange={this.onSearchHandler}
              value={term}
              />
          </form>
        </div>
        <table className="table">
          <thead className="thead-light">
            <tr>
              <th>No</th>
              <th>Make</th>
              <th>Model</th>
              <th>Year</th>
              <th>Price</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {this.vehicleList()}
          </tbody>
        </table>
      </div>
    )
  }
}
